import { useEffect, useState } from "react";
import axios from "axios";
import { Table, Image } from 'react-bootstrap';

export default function Beranda() {
    const [Data, setData] = useState([]);

    useEffect(() => {
        const fetch = async () => {
            try {
                const response = await axios.get(
                    // Ganti dengan endpoint YouTube API untuk video teknologi
                    `https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=10&q=technology&key=YOUR_YOUTUBE_API_KEY`
                );

                setData(response.data.items);
            } catch (error) {
                console.error(error);
            }
        }

        fetch();
    }, []);

    console.log(Data);

    return (
        <>
            <Table striped bordered hover responsive>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Thumbnail</th>
                    </tr>
                </thead>
                <tbody>
                    {Data.map((video, index) => (
                        <tr key={index}>
                            <td>
                                {video.snippet.title}
                            </td>
                            <td>
                                {video.snippet.description}
                            </td>
                            <td>
                                <Image width="50px" height="50px" src={video.snippet.thumbnails.default.url} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            Ini Outlet Beranda
        </>
    );
}
