import React, { useState, useContext } from "react";
import { Form, Button, Container, Alert } from 'react-bootstrap';

const MessageContext = React.createContext();

export default function Kontak() {
    const [nama, setNama] = useState('');
    const [email, setEmail] = useState('');
    const [showAlert, setShowAlert] = useState(false);

    const handleNamaChange = (event) => {
        setNama(event.target.value);
        setShowAlert(false); // Menyembunyikan notifikasi setelah perubahan input
    }

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
        setShowAlert(false); // Menyembunyikan notifikasi setelah perubahan input
    }

    const isFormValid = () => {
        return nama.trim() !== "" && email.trim() !== "";
    }

    const handleAlertClose = () => {
        setShowAlert(false);
    }

    const handleAlertShow = () => {
        setShowAlert(true);
    }

    return (
        <Container>
            <h2>
                Kontak Kami
            </h2>
            <Form.Label>Nama</Form.Label>
                <Form.Control type="text" placeholder="Masukkan Nama" value={nama} onChange={handleNamaChange} />
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="nama@gmail.com" value={email} onChange={handleEmailChange} />
            </Form.Group>
            Pesan :
            <MessageProvider>
                <MessagesForm isFormValid={isFormValid} showAlert={handleAlertShow} />
                <MessageList />
            </MessageProvider>
            <Alert variant="danger" show={showAlert} onClose={handleAlertClose} dismissible>
                Nama dan email harus diisi!
            </Alert>
        </Container>
    );
}

function MessageProvider({ children }) {
    const [messages, setMessages] = useState([]);
    return (
        <MessageContext.Provider value={{ messages, setMessages }} >
            {children}
        </MessageContext.Provider>
    )
}

function MessagesForm({ isFormValid, showAlert }) {
    const { messages, setMessages } = useContext(MessageContext);
    const [newMessages, setNewMessages] = useState('');

    const handleInputChange = (event) => {
        setNewMessages(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        if (!isFormValid() || !newMessages.trim()) {
            showAlert(); // Menampilkan notifikasi jika input tidak valid
            return;
        }

        const newMessageObject = {
            text: newMessages,
        };

        setMessages([...messages, newMessageObject]);
        setNewMessages("");
    }

    return (
        <>
            <Form>
                <Form.Group>
                    <Form.Control type="text" onChange={handleInputChange} value={newMessages} />
                    <Button className="btn btn-primary" onClick={handleSubmit} disabled={!isFormValid()}>
                        Kirim
                    </Button>
                </Form.Group>
            </Form>
        </>
    );
}

function MessageList() {
    const { messages } = useContext(MessageContext);

    return (
        <>
            <h2>
                Teks yang berhasil dikirim :
            </h2>
            {messages.map((message, index) => (
                <p key={index}>{message.text}</p>
            ))}
        </>
    );
}
