import React, { useState, useContext } from "react";
import { Form, Button, Container } from 'react-bootstrap';

const MessageContext = React.createContext();

export default function Contact() {
    return (
        <Container>
            Masukkan Text :
            <MessageProvider>
                <MessagesForm />
                <MessageList />
            </MessageProvider>
        </Container>
    );
}

function MessageProvider({ children }) {
    const [messages, setMessages] = useState([]);
    return (
        <MessageContext.Provider value={{ messages, setMessages }} >
            {children}
        </MessageContext.Provider>
    )
}

function MessagesForm() {
    const { messages, setMessages } = useContext(MessageContext);
    const [newMessages, setNewMessages] = useState('');

    const handleInputChange = (event) => {
        setNewMessages(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        if (!newMessages.trim === "") return;

        const Object = {
            text: newMessages,
        }

        setMessages([...messages, Object])
        setNewMessages("");
    }

    return (
        <>
            <Form>
                <Form.Group>
                    <Form.Control type="text" onChange={handleInputChange} value={newMessages} />
                    <Button className="btn btn-primary" onClick={handleSubmit}>Kirim</Button>
                </Form.Group>
            </Form>
        </>
    );
}

function MessageList() {
    const { messages } = useContext(MessageContext);

    return (
        <>
            <h2>
                Teks berhasil dikirim
            </h2>
            {messages.map((message, index) => (
                <p key={index}>{message.text}</p>
            ))}
        </>
    );
}
