import React, { useState, useContext } from "react";
import { Form, Button, Container, Alert } from 'react-bootstrap';

const MessageContext = React.createContext();

export default function Kontak() {
    return (
        <Container>
            <h2>Kontak Kami</h2>
            <Form>
                <Form.Group controlId="formNama">
                    <Form.Label>Nama</Form.Label>
                    <Form.Control type="text" placeholder="Masukkan Nama" />
                </Form.Group>
                <Form.Group controlId="formEmail">
                    <Form.Label>Email</Form.Label>
                    <Form.Control type="email" placeholder="nama@gmail.com" />
                </Form.Group>
                <MessageProvider>
                    <MessagesForm />
                    <MessageList />
                </MessageProvider>
            </Form>
        </Container>
    );
}

function MessageProvider({ children }) {
    const [messages, setMessages] = useState([]);
    return (
        <MessageContext.Provider value={{ messages, setMessages }} >
            {children}
        </MessageContext.Provider>
    )
}

function MessagesForm() {
    const { messages, setMessages } = useContext(MessageContext);
    const [newMessages, setNewMessages] = useState('');
    const [showAlert, setShowAlert] = useState(false);

    const handleInputChange = (event) => {
        setNewMessages(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        const name = document.getElementById("formNama").value;
        const email = document.getElementById("formEmail").value;

        if (!name.trim() || !email.trim()) {
            setShowAlert(true);
            return;
        }

        const formattedEmail = `${name}@gmail.com`;

        const newMessageObject = {
            text: newMessages,
            email: formattedEmail,
        }

        setMessages([...messages, newMessageObject])
        setNewMessages("");
    }

    return (
        <>
            <Form>
                <Form.Group>
                    <Form.Control type="text" onChange={handleInputChange} value={newMessages} />
                    <Button className="btn btn-primary" onClick={handleSubmit}>Kirim</Button>
                </Form.Group>
            </Form>
            {showAlert && <Alert variant="danger">Harap isi nama dan email!</Alert>}
        </>
    );
}

function MessageList() {
    const { messages } = useContext(MessageContext);

    return (
        <>
            <h2>Teks yang berhasil dikirim:</h2>
            {messages.map((message, index) => (
                <p key={index}>{`Email: ${message.email}, Text: ${message.text}`}</p>
            ))}
        </>
    );
}
