import React, { useState, useContext } from "react";
import { Form, Button, Container, Alert } from 'react-bootstrap';

const MessageContext = React.createContext();

export default function Kontak() {
    return (
        <Container>
            <h2>Kontak Kami</h2>
            <Form.Label>Nama</Form.Label>
            <Form.Control type="text" placeholder="Masukkan Nama" />
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="nama@gmail.com" />
            </Form.Group>
            Pesan :
            <MessageProvider>
                <MessagesForm />
                <MessageList />
            </MessageProvider>
        </Container>
    );
}

function MessageProvider({ children }) {
    const [messages, setMessages] = useState([]);
    return (
        <MessageContext.Provider value={{ messages, setMessages }}>
            {children}
        </MessageContext.Provider>
    );
}

function MessagesForm() {
    const { messages, setMessages } = useContext(MessageContext);
    const [newMessages, setNewMessages] = useState('');
    const [showError, setShowError] = useState(false);

    const handleInputChange = (event) => {
        setNewMessages(event.target.value);
        setShowError(false); // Menyembunyikan notifikasi setelah perubahan input
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        const namaValue = document.querySelector('input[type="text"]').value;
        const emailValue = document.querySelector('input[type="email"]').value;

        if (!namaValue.trim() || !emailValue.trim() || !newMessages.trim()) {
            setShowError(true); // Menampilkan notifikasi jika pesan, nama, atau email kosong
            return;
        }

        const newMessageObject = {
            text: newMessages,
        };

        setMessages([...messages, newMessageObject]);
        setNewMessages("");
    }

    return (
        <>
            <Form>
                <Form.Group>
                    <Form.Control type="text" placeholder="Masukkan Pesan" onChange={handleInputChange} value={newMessages} />
                </Form.Group>
                <Form.Group>
                    <Form.Control type="email" placeholder="nama@gmail.com" />
                </Form.Group>
                <Button className="btn btn-primary" onClick={handleSubmit} disabled={showError}>
                    Kirim
                </Button>
            </Form>
            <Alert variant="danger" show={showError} onClose={() => setShowError(false)} dismissible>
                Nama, email, dan pesan harus diisi!
            </Alert>
        </>
    );
}

function MessageList() {
    const { messages } = useContext(MessageContext);

    return (
        <>
            <h2>Teks yang berhasil dikirim :</h2>
            {messages.map((message, index) => (
                <p key={index}>{message.text}</p>
            ))}
        </>
    );
}
