import { useEffect, useState } from "react";
import axios from "axios";
import { Table, Image, Container, Row, Col } from 'react-bootstrap';

export default function Beranda() {
  const [Data, setData] = useState([]);

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get(
          `https://newsapi.org/v2/everything?q=tesla&from=2023-09-15&sortBy=publishedAt&apiKey=2cec721a651f4b1a80436877904cba3b`
        );
        setData(response.data.articles);
      } catch (error) {
        console.error(error);
      }
    };
    fetch();
  }, []);

  return (
    <Container className="mt-3">
      <h1>Tesla News</h1>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {Data.map((news, index) => (
            <tr key={index}>
              <td>{news.title}</td>
              <td>{news.description}</td>
              <td>
                {news.urlToImage && (
                  <Image width="50px" height="50px" src={news.urlToImage} />
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      {Data.length === 0 && <p>Loading...</p>}
    </Container>
  );
}
