import React, { useEffect, useState } from "react";
import axios from "axios";
import { Card, Button } from 'react-bootstrap';

const API_KEY = "YOUR_NEWS_API_KEY"; // Replace with your News API key

export default function Beranda() {
    const [data, setData] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(
                    `https://newsapi.org/v2/top-headlines?category=technology&apiKey=${API_KEY}`
                );
                setData(response.data.articles);

            } catch (error) {
                console.error(error);
            }
        }
        fetchData();
    }, []);

    const handleNext = () => {
        setCurrentIndex((prevIndex) => (prevIndex + 1) % data.length);
    };

    if (!data.length) {
        return <p>Loading...</p>;
    }

    const currentNews = data[currentIndex];

    return (
        <div>
            <Card style={{ width: '50rem', margin: '20px' }}>
                <Card.Img variant="top" src={currentNews.urlToImage} />
                <Card.Body>
                    <Card.Title>{currentNews.title}</Card.Title>
                    <Card.Text>{currentNews.description}</Card.Text>
                    <Card.Text>{currentNews.content}</Card.Text>
                    <Card.Text>Author: {currentNews.author}</Card.Text>
                    <Card.Text>Published At: {currentNews.publishedAt}</Card.Text>
                </Card.Body>
            </Card>
            <Button variant="primary" onClick={handleNext}>
                Berikutnya..
            </Button>
        </div>
    );
}
