import { useEffect, useState } from "react";
import axios from "axios";
import { Table, Image } from 'react-bootstrap';

export default function Home() {
    const [Data, setData] = useState([]);

    useEffect(() => {
        const fetch = async () => {
            try {
                const response = await axios.get(
                    `https://newsapi.org/v2/everything?q=technology&from=2023-09-15&sortBy=publishedAt&apiKey=YOUR_NEWSAPI_KEY`
                );
                setData(response.data.articles);
            } catch (error) {
                console.error(error);
            }
        };
        fetch();
    }, []);

    return (
        <>
            <Table striped bordered hover responsive>
                <thead>
                    <tr>
                        <th>Title</th>
                        <th>Description</th>
                        <th>Image</th>
                    </tr>
                </thead>
                <tbody>
                    {Data.map((news, index) => (
                        <tr key={index}>
                            <td>{news.title}</td>
                            <td>{news.description}</td>
                            <td>
                                <Image width="50px" height="50px" src={news.urlToImage} alt={news.title} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </>
    );
}
