import React, { useState, useContext } from "react";
import { Form, Button, Container } from 'react-bootstrap';

const MessageContext = React.createContext();

export default function Kontak() {
    return (
        <Container>
            <h2>
                Kontak Kami
            </h2>
            <Form.Label>Nama</Form.Label>
            <Form.Group className="mb-3">
                <Form.Control type="text" placeholder="Masukkan Nama" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="nama@gmail.com" />
            </Form.Group>
            Pesan :
            <MessageProvider>
                <MessagesForm />
                <MessageList />
            </MessageProvider>
        </Container>
    );
}

function MessageProvider({ children }) {
    const [messages, setMessages] = useState([]);
    return (
        <MessageContext.Provider value={{ messages, setMessages }}>
            {children}
        </MessageContext.Provider>
    )
}

function MessagesForm() {
    const { messages, setMessages } = useContext(MessageContext);
    const [newMessages, setNewMessages] = useState('');
    const [nameError, setNameError] = useState('');
    const [emailError, setEmailError] = useState('');
    const [messageError, setMessageError] = useState('');

    const handleInputChange = (event) => {
        setNewMessages(event.target.value);
    }

    const validateForm = () => {
        let isValid = true;

        // Validate name
        if (newMessages.trim() === "") {
            setNameError('Nama harus diisi');
            isValid = false;
        } else {
            setNameError('');
        }

        // Validate email
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(newMessages)) {
            setEmailError('Format email tidak valid');
            isValid = false;
        } else {
            setEmailError('');
        }

        // Validate message
        if (newMessages.trim() === "") {
            setMessageError('Pesan harus diisi');
            isValid = false;
        } else {
            setMessageError('');
        }

        return isValid;
    }

    const handleSubmit = (event) => {
        event.preventDefault();

        if (!validateForm()) {
            return;
        }

        const newMessageObject = {
            text: newMessages,
        }

        setMessages([...messages, newMessageObject]);
        setNewMessages("");
    }

    return (
        <>
            <Form>
                <Form.Group>
                    <Form.Control type="text" onChange={handleInputChange} value={newMessages} />
                    <small className="text-danger">{nameError}</small>

                    <Button className="btn btn-primary" onClick={handleSubmit}>Kirim</Button>
                </Form.Group>
            </Form>
        </>
    );
}

function MessageList() {
    const { messages } = useContext(MessageContext);

    return (
        <>
            <h2>
                Teks yang berhasil dikirim :
            </h2>
            {messages.map((message, index) => (
                <p key={index}>{message.text}</p>
            ))}
        </>
    );
}
